    //       // ...
    //     }
    //     fetchData();
    //   }, [someId]); // Or [] if effect doesn't need props or state

    useEffect(() => {
        getAllItems();
    }, [isLoading]);


    if (items.length != 0) {
        return (
            <div>
                {items.map((item, index) => {
                        <div
                            key={index}>
                                <div>
                                    </div>
                            {item.name}
                        </div>
                })}
            </div>
        )
    } else {
        return <div>Loading</div>
    }
