import { SimpleGrid, Box, Text } from "@chakra-ui/react";
import { useEffect, useState } from "react";
import API from "../api/index";

const Item = () => {

    const [items, setItems] = useState();

    const getListItems = () => {
        API.getItems()
            .then((res) => {
                console.log(res.data);
                setItems(res.data);
            })
            .catch((err) => {
                console.log(err)
            });
    }

    useEffect(async () => {
       await getListItems();
    }, []);

    return (
<div>
            {items ? (items.map((item) => {
                <div>Hello {item.data.name}</div>
            })
    ) : ( 
        <div>Hello</div>
    )}
        </div>
    )

}

export default Item;
