import { SimpleGrid, Box, Text } from "@chakra-ui/react";
import { useEffect, useState } from "react";
import API from "../api/index";

const Item = () => {

    const [items, setItems] = useState();

    useEffect(() => {
        API.getItems()
        .then((res) => {
            console.log(res.data);
            setItems(res.data);
        })
        .catch((err) => {
            console.log(err)
        });
    });

    return(
        <SimpleGrid
        columns={{ sm: 1, md: 3 }} spacing={10}>
            {items.map((item) => {
                <Box>{item.name}</Box>
            })}

        </SimpleGrid>
    )

}

export default Item;
